#include <SensirionI2CSfm3000.h>
#include <Wire.h>

SensirionI2CSfm3000 sfm;


float scalingFactor = 140.0;
float offset = 32000;


void setup() {

    Serial.begin(115200);
    while (!Serial) {
        delay(100);
    }

    Wire.begin();

    uint16_t error;
    char errorMessage[64];

    sfm.begin(Wire, SFM300_I2C_ADDRESS_0);

