    if (error) {
        Serial.print("Error trying to execute readMeasurement(): ");
        errorToString(error, errorMessage, 64);
        Serial.println(errorMessage);
    } else {
        Serial.print("Flow:");
        Serial.print(flow);
        Serial.println();
    }
}
